
public class Expressions {

	public static void main(String[] args) {
		int a, b, c, d;
		// TODO Auto-generated method stub
		a = 3 + (5 / 7); // 3
		b = 3 * 3 + 3 % 2; // 10
		c = 3 + 2 / 5 + -2 * 4; // -5
		d = 2 * (1 + -(3/4) / 2) * (2 - 6 % 3); // 4
		System.out.println("a = " + a + ", b = " + b + ", c = " + c + ", d = " + d);
	}
}
